function move(){document.querySelectorAll(".jbParallaxObj").forEach(function(t,e){var n;n=document.documentElement.scrollTop?t.getAttribute("jbInitY")-document.documentElement.scrollTop*t.getAttribute("parallaxSpeed")/100:t.getAttribute("jbInitY")-document.body.scrollTop*t.getAttribute("parallaxSpeed")/100,t.style.transform=t.style.msTransform=t.style.WebkitTransform="translate("+t.getAttribute("jbInitX")+"px, "+n+"px)"})}function prepare(){document.querySelectorAll(".jbParallaxObj").forEach(function(t,e){var n=t.style.transform,o=[0,0];if(n){for(i=n.indexOf("translate(")+10;")"!=n[i];i++);o=n.substring(n.indexOf("translate(")+10,i-2).split("px, "),console.log(o)}t.setAttribute("jbInitX",o[0]),t.setAttribute("jbInitY",o[1])})}window.onload=function(){prepare(),move()},window.onscroll=function(){move()};
